import { useState } from "react";
    const Welcome= ({el}) =>{
    const [displayMessage, setdisplayMessage] = useState(false);
    const handleClick = () => {
            
            console.log("click");
            setdisplayMessage(true);
           };
   
       return(
   
   <section className="welcome-section">
   
         <button onClick={handleClick}>Bienvenue</button>
         {displayMessage && (
            <div>
                 <h2>{el.name}</h2>
            </div>
        
          )}
   </section>
     );
   };
   
   
   export default Welcome
